import React, { useState, useEffect } from 'react';
import './ResourceForm.css';
import axios from 'axios';

const ResourceForm = () => {
  const [fullName, setFullName] = useState('');
  const [resume, setResume] = useState(null);
  const [vendorName, setVendorName] = useState('');
  const [technologies, setTechnologies] = useState([]);
  const [vendors, setVendors] = useState([]);

  // Fetch vendors from the backend
  useEffect(() => {
    const fetchVendors = async () => {
      try {
        const response = await axios.get('/api/vendors');
        setVendors(response.data);
      } catch (error) {
        console.error('Error fetching vendors:', error.message);
      }
    };
    fetchVendors();
  }, []);

  const handleTechnologyChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setTechnologies((prev) => [...prev, value]);
    } else {
      setTechnologies((prev) => prev.filter((tech) => tech !== value));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('fullName', fullName);
    formData.append('resume', resume);
    formData.append('vendorName', vendorName);
    formData.append('technologies', technologies);

    try {
      const response = await axios.post('/api/resources/add', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('Resource added successfully!');
      console.log('Resource added:', response.data);
    } catch (error) {
      console.error('Error submitting form:', error.message);
      alert('Failed to add resource. Please try again.');
    }
  };

  return (
    <div className="resource-form-container">
      <h2>Enter Resource Details</h2>
      <p>Enter resource information here</p>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            placeholder="Enter full name"
            required
          />
        </div>

        <div className="form-group">
          <label>Upload Resume</label>
          <input
            type="file"
            onChange={(e) => setResume(e.target.files[0])}
            required
          />
          <p className="drop-instruction">Attach file or drop files here</p>
        </div>

        <div className="form-group">
          <label>Vendor Name</label>
          <select
            value={vendorName}
            onChange={(e) => setVendorName(e.target.value)}
            required
          >
            <option value="">Select Vendor</option>
            {vendors.map((vendor) => (
              <option key={vendor._id} value={vendor.name}>
                {vendor.name}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label>Technologies</label>
          <div className="checkbox-group">
            {['Node.js', 'PHP', 'React.js', 'TypeScript', 'Express.js'].map((tech) => (
              <div key={tech} className="checkbox-item">
                <input
                  type="checkbox"
                  value={tech}
                  onChange={handleTechnologyChange}
                />
                <label>{tech}</label>
              </div>
            ))}
          </div>
        </div>

        <button type="submit" className="submit-button">Submit</button>
      </form>
    </div>
  );
};

export default ResourceForm;
