import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourceForm = () => {
  const [vendors, setVendors] = useState([]);
  const [formData, setFormData] = useState({ fullName: '', vendorName: '', technologies: '' });
  const [resume, setResume] = useState(null);

  useEffect(() => {
    const fetchVendors = async () => {
      const response = await axios.get('');
      setVendors(response.data);
    };
    fetchVendors();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append('fullName', formData.fullName);
    data.append('vendorName', formData.vendorName);
    data.append('technologies', formData.technologies);
    data.append('resume', resume);

    await axios.post('', data);
    alert('Resource added!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Full Name"
        onChange={(e) => setFormData({ ...formData, fullName: e.target.value })}
        required
      />
      <input
        type="file"
        onChange={(e) => setResume(e.target.files[0])}
        required
      />
      <select
        onChange={(e) => setFormData({ ...formData, vendorName: e.target.value })}
        required
      >
        <option value="">Select Vendor</option>
        {vendors.map((vendor) => (
          <option key={vendor._id} value={vendor.name}>{vendor.name}</option>
        ))}
      </select>
      <input
        type="text"
        placeholder="Technologies (comma-separated)"
        onChange={(e) => setFormData({ ...formData, technologies: e.target.value })}
        required
      />
      <button type="submit">Add Resource</button>
    </form>
  );
};

export default ResourceForm;
